<?phpfunction generatePetitionBlock($signatures, $total_signatures, $rel_to_root = './'){	$html = '<div class="signatures_petition">				<h1>Signataires pour cette proposition :</h1>				<table>';		if($total_signatures > 0){			foreach($signatures as $key => $signature){				$html .= generateSignature($signature, $rel_to_root);			}		}		else{			$html .= '<td>Pas encore de signataires pour cette proposition !</td>';		}	$html .= '</table>				<p>					<span style="font-weight: bold;">Pourquoi signer une proposition ?</span><br/>					Les signatures, surtout lorsqu\'elles se font en nom propre, permettent de donner beaucoup plus de crédibilité à la proposition.					C\'est en quelque sorte, un engagement suppérieur à un simple vote.				</p>			</div>';	return $html;}function generateSignature($signature, $rel_to_root = './'){	$html = '<tr>			<td>'.$signature['no'].'.</td>			<td>'; 				if($signature['site'] == ''){$html .= $signature['prenom'].' '.$signature['nom'];}				else{$html .= '<a target="_blanck" href="'.$signature['site'].'" title="'.$signature['site'].'">'.$signature['prenom'].' '.$signature['nom'].'</a>';}			$html .= '</td>			<td> - </td>			<td>'.$signature['profession'].'</td>			<td> - </td>			<td>'.$signature['message'].'</td>		</tr>';	return $html;}?>