<?phpfunction generatePetitionBlock($signatures, $total_signatures, $rel_to_root = './'){	$html = '<div class="signatures_petition">				<h1>Signataires pour cette proposition :</h1>				<table>';		if($total_signatures > 0){			foreach($signatures as $key => $signature){				$html .= generateSignature($signature, $rel_to_root);			}		}		else{			$html .= '<td>Pas encore de signataires pour cette proposition !</td>';		}	$html .= '</table>				<p>					<span style="font-weight: bold;">Pourquoi signer une proposition ?</span><br/>					Les signatures, surtout lorsqu\'elles se font en nom propre, permettent de donner beaucoup plus de crédibilité à la proposition.					C\'est en quelque sorte, un engagement suppérieur à un simple vote.				</p>			</div>';	return $html;}function generateSignature($signature, $rel_to_root = './'){	$html = '<tr>			<td>'.$signature['no'].'.</td>			<td>'.$signature['prenom'].' '.$signature['nom'].'</td>			<td> - </td>			<td>'.$signature['message'].'</td>		</tr>';	return $html;}function generatePetitionPager($nbPages, $current, $baseUrl, $anchor){	$widthPager = 4;		$html = '<div class="petition_pager">';	// previous	if($current == 1){$html .= '<span>< Précédent</span> <b></b> ';}	else{$prev = $current-1; $html .= '<a href="'.$baseUrl.''.$prev.$anchor.'">< Précédent</a> <b></b> ';}		// first	if($current - $widthPager - 1 == 1){		$html .= '<a href="'.$baseUrl.'1'.$anchor.'">1</a> <b></b> ';	}	else if($current - $widthPager > 1){		$html .= '<a href="'.$baseUrl.'1'.$anchor.'">1</a> <b></b> ... <b></b> ';	}		// middle	for($i=$current-$widthPager; $i<=$current+$widthPager; $i++){		if($i == $current){			$html .= '<strong class="med">'.$i.'</strong> <b></b> ';		}		else if($i > 0 && $i <= $nbPages){			$html .= '<a href="'.$baseUrl.''.$i.$anchor.'">'.$i.'</a> <b></b> ';		}	}		// last	if($current + $widthPager + 1 == $nbPages){		$html .= '<a href="'.$baseUrl.''.$nbPages.$anchor.'">'.$nbPages.'</a> <b></b> ';	}	else if($current + $widthPager < $nbPages){		$html .= '... <b></b> <a href="'.$baseUrl.''.$nbPages.$anchor.'">'.$nbPages.'</a> <b></b> ';	}		// next	if($current == $nbPages){$html .= '<span>Suivant ></span>';}	else{$next = $current+1; $html .= '<a href="'.$baseUrl.''.$next.$anchor.'">Suivant ></a>';}		$html .= '</div>';	return $html;}?>