<?php	$rel_to_root = './';	include $rel_to_root.'inc/server_link.php';		$new_quote = sendQuoteForm($usr);	if(isset($new_quote['status']['code']) && $new_quote['status']['code'] == 200){		// TODO : post quote to twitter...		header('Location: quote.php?id='.$new_quote['response']['id']);   		exit();	}		$cat = null;	$categories = api_call('GET', $usr['api_url'].'category.php', array('key'=>$usr['key']), false );	if(isset($categories['response']['categories'])){		$ind = 0;		foreach($categories['response']['categories'] as $key => $value){			$cat[$ind]['id'] = $value['id'];			$cat[$ind]['name'] = $value['name'];			$ind++;		}				for($i=2; $i<=$categories['response']['total_category_pages']; $i++){ // récupération des autres pages de catégories...			$categories = api_call('GET', $usr['api_url'].'category.php', array('key'=>$usr['key'], 'p'=>$i), false );			foreach($categories['response']['categories'] as $key => $value){				$cat[$ind]['id'] = $value['id'];				$cat[$ind]['name'] = $value['name'];				$ind++;			}		}	}?><?php echo generateHead(' - Nouvelle proposition', $jsEnv); ?><body>	<?php echo generateHeader(); ?>		<div id="main">		<div class="wrapper">			<?php echo generateQuoteForm($usr, $new_quote, $cat); ?>		</div>	</div>		<?php echo generateFooter(); ?>		<?php echo generateScriptsJs(); ?>	<?php		if(isset($new_quote) && isset($new_quote['status']['code']) && $new_quote['status']['code'] != 200){			echo '<script>displayInfo("error", "Erreur ('.$new_quote['status']['code'].') lors de la sauvegarde de la citation");</script>';		}	?>	<?php 		include $rel_to_root.'fragments/endPage.php';		echo generateEndPage(); 	?></body></html>