<!doctype html><head>	<meta charset="UTF-8">	<meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1">		<title>get Json with PHP</title></head><body><?php	function http_post($url, $data){		$data_url = http_build_query($data);		$data_len = strlen ($data_url);		return file_get_contents($url, false, 				stream_context_create(					array(						'http'=>array(							'method'=>	'POST',							'header'=>	"Connection: close\r\n".										"Content-Type: application/x-www-form-urlencoded\r\n". 										"Content-Length: ".$data_len."\r\n",							'content'=>	$data_url						)							)				)			);	}		function http_get($url, $data){		$params = '';		if($data != null){			$cpt = 0;			foreach($data as $key => $value){				if($cpt == 0){$params .= '?'.$key.'='.$value;}				else{$params .= '&'.$key.'='.$value;}				$cpt++;			}		}		return file_get_contents($url.$params);	}		function call_api($method, $url, $data){		if($method == 'POST'){return http_post($url, $data);}		else if($method == 'GET'){return http_get($url, $data);}		else{return false;}	}		/*	$file = call_api('POST', "http://localhost/lkws_croudquotes/api/1.0/test.php", array('test'=>'toto'));	if($file){		echo $file.'<br/>';		$result = json_decode($file);		if($result != null){			echo '<br/>var: "'.$result->test.'", exec meth: "'.$result->cond.'", real meth: "'.$result->methode.'"<br/>';		}		else{			echo '<br/>Can\'t decode json !<br/>';		}	}	else{		echo 'Can\'t get file !<br/>';	}*/		$file = call_api('GET', "http://localhost/lkws_croudquotes/api/1.0/quote.php", array('key'=>'aff83ccf08f1dd28ee330b0b07de4f3b985a81d4', 'quoteid'=>'2'));	if($file){		echo $file.'<br/>';		$result = json_decode($file);		if($result != null){			echo '<br/>Quote content: "'.$result->response->quote.'", author: "'.$result->response->author.'"<br/>';		}		else{			echo '<br/>Can\'t decode json !<br/>';		}	}	else{		echo 'Can\'t get file !<br/>';	}?></body></html>